                g.CopyFromScreen\(r.Left, r.Top, 0, 0, new Size\(w, h\)\);
            }
            bmp.Save\(path, ImageFormat.Png\);
            Console.WriteLine\(""Captured: "" + w + ""x"" + h\);
        }
    }
}
